
public class SwapNumbers {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=6;
		System.out.printf("Values of a and b before swapping are %d and %d \n",a,b);
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.printf("Values of a and b after swapping are  %d and %d\n",a,b);

	}

}
